(()=>{"use strict";const e={destination:"Paris, France",departureDate:"02/12/2025",daysAway:220,image:"https://images.pexels.com/photos/338515/pexels-photo-338515.jpeg",weather:{high:46,low:35,description:"Mostly cloudy"},flight:{number:"ORD 300PM",departureTime:"3:00 PM",airline:"UDCITY AIR"}};var n,t,i;document.getElementById("app").innerHTML=`\n    \n        <header>\n            <h1>Travel Planner</h1>\n        </header>\n    \n    <main>\n        \n        <button class="add-trip">+ Add Trip</button>\n    \n        ${i=e,`\n        <div class="trip-card">\n            <h2>${i.destination}</h2>\n            <img src="${i.image}" alt="Trip image" style="width: 300px; height: auto;">\n            <p>Departure Date: ${i.departureDate}</p>\n            <p>Days Away: ${i.daysAway}</p>\n            <p>Weather: ${i.weather.description}, High: ${i.weather.high}째C, Low: ${i.weather.low}째C</p>\n            <p>Flight Info: ${i.flight.number}, ${i.flight.departureTime}, ${i.flight.airline}</p>\n        </div>\n    `}\n        ${t=e.weather,`\n        <div class="weather-info">\n            <p><strong>Weather Forecast:</strong></p>\n            <p>High: ${t.high}째C, Low: ${t.low}째C</p>\n            <p>${t.description}</p>\n        </div>\n    `}\n        ${n=e.flight,`\n      <div class="flight-info">\n          <p><strong>Flight Details:</strong></p>\n          <p>Flight Number: ${n.number}</p>\n          <p>Departure: ${n.departureTime}</p>\n          <p>Airline: ${n.airline}</p>\n      </div>\n  `}\n    </main>\n`,document.addEventListener("DOMContentLoaded",(()=>{const e=document.getElementById("addTripBtn");e?e.addEventListener("click",(()=>{alert("Add Trip button clicked!")})):console.error("Button not found in the DOM.")}))})();